// Function node: parse fenced JSON in item.json.text and map BM -> MY
const out = [];

for (const item of $input.all()) {
  const raw = item.json.text;

  // 1) ambil content dalam ```json ... ```
  const m = typeof raw === 'string' && raw.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
  const jsonText = m ? m[1] : raw; // fallback kalau takde fence

  let parsed = {};
  try {
    parsed = JSON.parse(jsonText);
  } catch (err) {
    // kalau parse gagal, return error flag supaya senang debug
    item.json._parseError = err.message;
    out.push(item);
    continue;
  }

  // 2) map keys — contoh: EN stays EN, BM -> MY (atau gunakan MY jika dah wujud)
  const EN = parsed.EN ?? parsed.En ?? parsed.en ?? '';
  const MY = parsed.MY ?? parsed.BM ?? parsed.Bm ?? parsed.my ?? '';

  // 3) bentuk output structure yg kau mahu
  const newItem = {
    json: {
      text: {
        EN,
        MY,
      },
    },
  };

  out.push(newItem);
}

return out;

